import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductservService } from '../../services/productserv.service';
import { CartService, CartItem } from '../../services/cart.service';
import { CommonModule } from '@angular/common';
declare var bootstrap: any;

@Component({
  selector: 'app-description-page',
  templateUrl: './description-page.component.html',
  styleUrls: ['./description-page.component.css'],
  imports: [CommonModule],
  standalone: true,
})
export class DescriptionPageComponent implements OnInit {
  product: any;
  similarProducts: any[] = [];

  constructor(
    private route: ActivatedRoute,
    public router: Router,
    private productService: ProductservService,
    private cartService: CartService
  ) {}

  ngOnInit(): void {
    const productId = this.route.snapshot.paramMap.get('id');
    console.log('product id passed', productId);

    if (productId) {
      this.productService.getProductById(+productId).subscribe({
        next: (data) => {
          this.product = data; // already a single product
          console.log('Loaded product:', this.product);
        },
        error: (err) => console.error('Error fetching product:', err),
      });
    }
  }

  addToCart(product: any): void {
    this.cartService.addToCart(product);
    const modalElement = document.getElementById('cartModal');
    const modal = new bootstrap.Modal(modalElement);
    modal.show();
  }

  goToCart() {
    const modalElement = document.getElementById('cartModal');
    if (modalElement) {
      const modal = bootstrap.Modal.getInstance(modalElement);
      modal?.hide();
    }
    this.router.navigate(['/cart']);
  }

  viewProduct(id: number) {
    // Navigate to the product's description page
    this.router.navigate(['/products-page', id]);
  }


  goToProducts() {
    const modalElement = document.getElementById('cartModal');
    if (modalElement) {
      const modal = bootstrap.Modal.getInstance(modalElement);
      modal?.hide();
    }
    this.router.navigate(['/Product']);
  }

  Product(id: number) {
    // Navigate to the product's description page
    this.router.navigate(['/products-page', id]);
  }

}
<div class="container my-5" *ngIf="product; else loading">
  <div class="row">
    <!-- Product Image -->
    <div class="col-md-5 text-center">
      <img
        [src]="product.image"
        [alt]="product.title"
        class="img-fluid rounded shadow-sm"
        style="max-height: 400px; object-fit: contain;"
      />
    </div>

    <!-- Product Info -->
    <div class="col-md-7">
      <h2 class="mb-3">{{ product.title }}</h2>
      <p class="text-muted mb-2">{{ product.category }}</p>
      <h4 class="text-primary mb-3">R{{ product.price }}</h4>
      <p class="mb-4">{{ product.description }}</p>

      <button class="btn btn-success me-2" (click)="addToCart(product)">
        Add to Cart
      </button>
      <button class="btn btn-outline-secondary" (click)="router.navigate(['/products'])">
        Back to Products
      </button>
    </div>
  </div>

  <!-- Similar Products -->
  <div class="similar-section mt-5" *ngIf="similarProducts.length > 0">
    <h3 class="mb-4">Similar Products</h3>
    <div class="row g-3">
      <div
        class="col-6 col-md-3"
        *ngFor="let p of similarProducts"
        style="cursor: pointer;"
      >
        <div class="card h-100 shadow-sm">
          <img
            [src]="p.image"
            class="card-img-top p-3"
            alt="{{ p.title }}"
            style="height: 150px; object-fit: contain;"
          />
          <div class="card-body p-2 text-center">
            <p class="card-title small text-truncate mb-1">{{ p.title }}</p>
            <p class="fw-bold mb-0">R{{ p.price }}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

<ng-template #loading>
  <div class="text-center my-5">
    <div class="spinner-border text-primary" role="status"></div>
    <p class="mt-3">Loading product details...</p>
  </div>
</ng-template>

<!-- Add to Cart Confirmation Modal -->
<div
  class="modal fade"
  id="cartModal"
  tabindex="-1"
  aria-labelledby="cartModalLabel"
  aria-hidden="true"
>
  <div class="modal-dialog modal-dialog-centered">
    <div class="modal-content">
      <div class="modal-header bg-success text-white">
        <h5 class="modal-title" id="cartModalLabel">Item Added to Cart</h5>
        <button
          type="button"
          class="btn-close btn-close-white"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body text-center">
        <p class="mb-3">
          ✅ <strong>{{ product.title }}</strong> has been added to your cart.
        </p>
      </div>
      <div class="modal-footer d-flex justify-content-center">
        <button
          class="btn btn-outline-secondary"
          data-bs-dismiss="modal"

        >
          Continue Shopping
        </button>
        <button
          class="btn btn-primary"
          (click)="goToCart()"
        >
          Go to Cart
        </button>
      </div>
    </div>
  </div>
</div>
