import { Component, OnInit } from '@angular/core';
import { ProductservService } from '../../services/productserv.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  standalone: false,
})
export class HomeComponent implements OnInit {
  products: any[] = [];
  currentSlide = 0;

  constructor(private productService: ProductservService) {}

  ngOnInit() {
    this.productService.getProducts().subscribe((data) => {
      this.products = data.slice(0, 5);
      this.startSlideshow();
    });
  }

  startSlideshow() {
    setInterval(() => {
      this.currentSlide = (this.currentSlide + 1) % this.products.length;
    }, 3000);
  }
}
<div class="home-container">
  <div class="slideshow-container" *ngIf="products.length > 0">
    <div
      class="slide"
      *ngFor="let product of products; let i = index"
      [class.active]="i === currentSlide"
      [style.background-image]="'url(' + product.image + ')'"
    ></div>
  </div>
  <section class="hero">
    <h1>Welcome to VibeWear</h1>
    <p>Discover amazing products at great prices</p>
    <button routerLink="/products" class="cta-button">Shop Now</button>
  </section>
</div>
