import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { ProductservService } from '../../services/productserv.service';
import { CartService } from '../../services/cart.service';

import { Router } from '@angular/router';
@Component({
  selector: 'product-page',
  imports: [CommonModule, RouterModule],
  templateUrl: './product-page.component.html',
  styleUrls: ['./product-page.component.css'],
  standalone: true,
})

// injected the API from services
export class ProductPageComponent implements OnInit {
  products: any[] = [];

  constructor(
    private productService: ProductservService,
    private cartService: CartService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.productService.getProducts().subscribe({
      next: (data) => {
        this.products = data;
      },
      error: (error) => {
        console.error('Error fetching products:', error);
      },
    });
  }

  addToCart(product: any): void {
    this.cartService.addToCart(product);
    // Optional: Show toast notification
    alert(`${product.title} added to cart!`);
  }

  onSelectProduct(productId: Number): void {
    console.log('Navigating to product ID:', productId);
    this.router.navigate(['products', productId]);
  }
}


<!-- The Products / probably gonna need to have this in its own component -->
<div class="container mt-4">
  <h2 class="text-center mb-4">Products</h2>

  <!-- Used Bootstrap to for the styling of the cards (an alternative to pure css)-->
  <div class="row g-4">
    <div
      class="col-12 col-sm-6 col-md-4 col-lg-3"
      *ngFor="let product of products"
    >
      <div class="card h-100 shadow-sm">
        <img
          [src]="product.image"
          class="card-img-top p-3"
          alt="{{ product.title }}"
          style="height: 200px; object-fit: contain"
        />
        <div class="card-body d-flex flex-column">
          <h5 class="card-title text-truncate">{{ product.title }}</h5>
          <p class="card-text text-muted small">
            {{ product.description | slice : 0 : 60 }}...
          </p>
          <p class="fw-bold mt-auto">Price: R{{ product.price }}</p>
          <button style="cl" class="btn btn-primary mt-2 w-100" (click)="onSelectProduct(product.id)">Select</button>
        </div>
      </div>
    </div>
  </div>
</div>
