import { Component } from '@angular/core';
import { CartService } from 'src/app/services/cart.service';
import { RouterModule,Router } from '@angular/router';

@Component({
  selector: 'app-check-out-pop-up',
  templateUrl: './check-out-pop-up.component.html',
  imports: [RouterModule],
  styleUrls: ['./check-out-pop-up.component.css'],
  standalone: true,
})
export class CheckOutPopUpComponent {
  OrderCode = Math.floor(100000 + Math.random() * 900000); // Generate a random 6-digit order code  

  constructor(private cart: CartService ,private router: Router) { } 


 //This will close the pop-up when the user clicks the close button
  ClosePopUp() {

    this.router.navigate(['/products']);
    this.cart.clearCart();
  } 
}
<div class="modal-dialog modal-dialog-centered">
    <div class="modal-content">
        <div class="modal-header">
        <h5 class="modal-title ">Checkout</h5>

        </div>
        <div class="modal-body">
        <p class="d-flex justify-content-between mt-3">Your order#{{OrderCode}} has been placed successfully!</p>   <!--on the # create Auto order function-->
        <p style="color: darkblue;"> Thank you for shopping with us.</p>
        </div>
        <div class="modal-footer">
        <button type="button" class="btn btn-primary" data-bs-dismiss="modal" (click)="ClosePopUp()" >Close</button>
        </div>
    </div>
</div>
